import{_ as e,c as t,o as a,a as o}from"./app.54f4c473.js";const v=JSON.parse('{"title":"Cloud Native","description":"","frontmatter":{},"headers":[{"level":2,"title":"Kubernetes","slug":"kubernetes"},{"level":2,"title":"Istio","slug":"istio"},{"level":2,"title":"Argo CD","slug":"argo-cd"},{"level":2,"title":"Argo Workflows","slug":"argo-workflows"},{"level":2,"title":"Knative","slug":"knative"}],"relativePath":"knowledge/cloud-native.md","lastUpdated":1661330083000}'),r={name:"knowledge/cloud-native.md"},s=o('<h1 id="cloud-native" tabindex="-1">Cloud Native <a class="header-anchor" href="#cloud-native" aria-hidden="true">#</a></h1><nav class="table-of-contents"><ul><li><a href="#kubernetes">Kubernetes</a></li><li><a href="#istio">Istio</a></li><li><a href="#argo-cd">Argo CD</a></li><li><a href="#argo-workflows">Argo Workflows</a></li><li><a href="#knative">Knative</a></li></ul></nav><h2 id="kubernetes" tabindex="-1">Kubernetes <a class="header-anchor" href="#kubernetes" aria-hidden="true">#</a></h2><p>Kubernetes \u662F\u4E00\u4E2A\u53EF\u79FB\u690D, \u53EF\u6269\u5C55\u7684\u5F00\u6E90\u5E73\u53F0, \u7528\u4E8E\u7BA1\u7406\u5BB9\u5668\u5316\u7684\u5DE5\u4F5C\u8D1F\u8F7D\u548C\u670D\u52A1, \u53EF\u4FC3\u8FDB\u58F0\u660E\u5F0F\u914D\u7F6E\u548C\u81EA\u52A8\u5316</p><div class="tip custom-block"><p class="custom-block-title">\u6587\u6863</p><p><a href="https://kubernetes.io/zh-cn/docs/home/" target="_blank" rel="noreferrer">https://kubernetes.io/zh-cn/docs/home/</a></p></div><h2 id="istio" tabindex="-1">Istio <a class="header-anchor" href="#istio" aria-hidden="true">#</a></h2><p>Istio \u662F\u4E00\u4E2A\u5B8C\u5168\u5F00\u6E90\u7684\u670D\u52A1\u7F51\u683C, \u4F5C\u4E3A\u900F\u660E\u7684\u4E00\u5C42\u63A5\u5165\u5230\u73B0\u6709\u7684\u5206\u5E03\u5F0F\u5E94\u7528\u7A0B\u5E8F\u91CC. \u5B83\u4E5F\u662F\u4E00\u4E2A\u5E73\u53F0, \u62E5\u6709\u53EF\u4EE5\u96C6\u6210\u4EFB\u4F55\u65E5\u5FD7, \u9065\u6D4B\u548C\u7B56\u7565\u7CFB\u7EDF\u7684 API \u63A5\u53E3. Istio \u591A\u6837\u5316\u7684\u7279\u6027\u4F7F\u60A8\u80FD\u591F\u6210\u529F\u4E14\u9AD8\u6548\u5730\u8FD0\u884C\u5206\u5E03\u5F0F\u5FAE\u670D\u52A1\u67B6\u6784, \u5E76\u63D0\u4F9B\u4FDD\u62A4, \u8FDE\u63A5\u548C\u76D1\u63A7\u5FAE\u670D\u52A1\u7684\u7EDF\u4E00\u65B9\u6CD5</p><div class="tip custom-block"><p class="custom-block-title">\u6587\u6863</p><p><a href="https://istio.io/latest/zh/docs/" target="_blank" rel="noreferrer">https://istio.io/latest/zh/docs/</a></p></div><h2 id="argo-cd" tabindex="-1">Argo CD <a class="header-anchor" href="#argo-cd" aria-hidden="true">#</a></h2><p>Application definitions, configurations, and environments should be declarative and version controlled. Application deployment and lifecycle management should be automated, auditable, and easy to understand.</p><div class="tip custom-block"><p class="custom-block-title">\u6587\u6863</p><p><a href="https://argo-cd.readthedocs.io/en/stable/" target="_blank" rel="noreferrer">https://argo-cd.readthedocs.io/en/stable/</a></p></div><h2 id="argo-workflows" tabindex="-1">Argo Workflows <a class="header-anchor" href="#argo-workflows" aria-hidden="true">#</a></h2><p>Argo Workflows is an open source container-native workflow engine for orchestrating parallel jobs on Kubernetes. Argo Workflows is implemented as a Kubernetes CRD (Custom Resource Definition).</p><div class="tip custom-block"><p class="custom-block-title">\u6587\u6863</p><p><a href="https://argoproj.github.io/argo-workflows/quick-start/" target="_blank" rel="noreferrer">https://argoproj.github.io/argo-workflows/quick-start/</a></p></div><h2 id="knative" tabindex="-1">Knative <a class="header-anchor" href="#knative" aria-hidden="true">#</a></h2><p>Knative is an Open-Source Enterprise-level solution to build Serverless and Event Driven Applications</p><div class="tip custom-block"><p class="custom-block-title">\u6587\u6863</p><p><a href="https://knative.dev/docs/getting-started/" target="_blank" rel="noreferrer">https://knative.dev/docs/getting-started/</a></p></div>',17),i=[s];function l(n,d,c,h,p,u){return a(),t("div",null,i)}const g=e(r,[["render",l]]);export{v as __pageData,g as default};
