import{_ as e,c as a,o as l,a as s}from"./app.f568c437.js";const k=JSON.parse('{"title":"Docker","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u6784\u5EFA\u8FC7\u7A0B","slug":"\u6784\u5EFA\u8FC7\u7A0B"},{"level":2,"title":"\u955C\u50CF\u7626\u8EAB","slug":"\u955C\u50CF\u7626\u8EAB"},{"level":2,"title":"\u4FE1\u606F\u67E5\u770B","slug":"\u4FE1\u606F\u67E5\u770B"}],"relativePath":"knowledge/docker.md","lastUpdated":1665297476000}'),n={name:"knowledge/docker.md"},r=s(`<h1 id="docker" tabindex="-1">Docker <a class="header-anchor" href="#docker" aria-hidden="true">#</a></h1><nav class="table-of-contents"><ul><li><a href="#\u6784\u5EFA\u8FC7\u7A0B">\u6784\u5EFA\u8FC7\u7A0B</a></li><li><a href="#\u955C\u50CF\u7626\u8EAB">\u955C\u50CF\u7626\u8EAB</a></li><li><a href="#\u4FE1\u606F\u67E5\u770B">\u4FE1\u606F\u67E5\u770B</a></li></ul></nav><h2 id="\u6784\u5EFA\u8FC7\u7A0B" tabindex="-1">\u6784\u5EFA\u8FC7\u7A0B <a class="header-anchor" href="#\u6784\u5EFA\u8FC7\u7A0B" aria-hidden="true">#</a></h2><p>Docker \u5B88\u62A4\u8FDB\u7A0B\u9010\u6761\u6267\u884C Dockerfile \u4E2D\u7684\u6307\u4EE4, \u7136\u540E\u5C06\u6BCF\u4E00\u6761\u6307\u4EE4\u6267\u884C\u7684\u7ED3\u679C\u63D0\u4EA4\u5230\u65B0\u7684\u955C\u50CF (\u5982\u679C\u6709\u5FC5\u8981\u7684\u8BDD), \u4E5F\u5C31\u662F\u6BCF\u4E00\u6761\u6307\u4EE4\u6267\u884C\u5B8C\u5982\u679C\u6709\u7ED3\u679C\u9700\u8981\u5199\u5230\u955C\u50CF\u4E2D, \u5219\u4F1A\u5728\u539F\u6765\u955C\u50CF\u7684\u57FA\u7840\u4E0A\u521B\u5EFA\u65B0\u7684\u4E00\u5C42, \u5F53\u6784\u5EFA\u5B8C\u6210\u540E\u8F93\u51FA\u65B0\u955C\u50CF\u7684 ID. \u6700\u7EC8, Docker \u5B88\u62A4\u8FDB\u7A0B\u81EA\u52A8\u6E05\u7406\u88AB\u53D1\u9001\u8FC7\u6765\u7684\u672C\u6B21\u6784\u5EFA\u7684\u4E0A\u4E0B\u6587\u6570\u636E</p><p>\u6CE8\u610F\u6BCF\u4E00\u4E2A\u6307\u4EE4\u90FD\u662F\u72EC\u7ACB\u6267\u884C\u7684, \u6BCF\u4E00\u6761\u6307\u4EE4\u90FD\u4F1A\u521B\u5EFA\u65B0\u7684\u4E00\u5C42, \u6240\u4EE5<code>RUN cd /tmp</code>\u5BF9\u4E0B\u4E00\u6761\u6307\u4EE4\u6CA1\u6709\u4EFB\u4F55\u5F71\u54CD. \u7531\u4E8E\u6BCF\u4E00\u6761\u6307\u4EE4\u90FD\u4F1A\u521B\u5EFA\u65B0\u7684\u4E00\u5C42, \u5982\u679C Dockerfile \u6307\u4EE4\u8FC7\u591A, \u4F1A\u5BFC\u81F4\u6784\u5EFA\u7684\u955C\u50CF\u4F53\u79EF\u8FC7\u5927, \u6240\u4EE5\u5C3D\u91CF\u51CF\u5C11\u4E0D\u5FC5\u8981\u7684\u6307\u4EE4, \u6216\u8005\u5C06\u591A\u6761\u547D\u4EE4\u5408\u5E76\u5230\u4E00\u6761\u6307\u4EE4\u4E2D</p><ul><li>\u53D1\u9001 context \u4E0A\u4E0B\u6587\u4E2D\u7684\u6587\u4EF6\u5230 Docker \u5B88\u62A4\u8FDB\u7A0B</li><li>\u8FDB\u884C Dockerfile \u6587\u4EF6\u5185\u5BB9\u7684\u9884\u6821\u9A8C</li><li>\u9010\u6761\u6267\u884C Dockerfile \u4E2D\u7684\u6307\u4EE4</li><li>\u6700\u7EC8\u751F\u6210\u955C\u50CF</li></ul><h2 id="\u955C\u50CF\u7626\u8EAB" tabindex="-1">\u955C\u50CF\u7626\u8EAB <a class="header-anchor" href="#\u955C\u50CF\u7626\u8EAB" aria-hidden="true">#</a></h2><p><strong>\u7CBE\u7B80\u5C42\u6570</strong></p><ul><li>RUN\u6307\u4EE4\u5408\u5E76</li><li>\u591A\u9636\u6BB5\u6784\u5EFA</li></ul><p><strong>\u7CBE\u7B80\u6BCF\u4E00\u5C42\u5927\u5C0F</strong></p><ul><li>\u4F7F\u7528\u5408\u9002\u7684\u57FA\u7840\u955C\u50CF (\u9996\u9009 alpine)</li><li>\u5220\u9664RUN\u7684\u7F13\u5B58\u6587\u4EF6</li></ul><h2 id="\u4FE1\u606F\u67E5\u770B" tabindex="-1">\u4FE1\u606F\u67E5\u770B <a class="header-anchor" href="#\u4FE1\u606F\u67E5\u770B" aria-hidden="true">#</a></h2><div class="language-shell"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#676E95;"># \u72B6\u6001\u67E5\u770B</span></span>
<span class="line"><span style="color:#A6ACCD;">docker stats</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;"># \u5E95\u5C42\u4FE1\u606F</span></span>
<span class="line"><span style="color:#A6ACCD;">docker inspect</span></span>
<span class="line"></span></code></pre></div>`,13),t=[r];function i(c,o,d,p,h,_){return l(),a("div",null,t)}const f=e(n,[["render",i]]);export{k as __pageData,f as default};
