import{_ as n,c as s,o as a,a as l}from"./app.ad198a87.js";const A=JSON.parse('{"title":"Linux","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u5E38\u7528\u547D\u4EE4","slug":"\u5E38\u7528\u547D\u4EE4"},{"level":3,"title":"chmod","slug":"chmod"},{"level":3,"title":"chown","slug":"chown"},{"level":3,"title":"chgrp","slug":"chgrp"}],"relativePath":"knowledge/linux.md","lastUpdated":1661673269000}'),o={name:"knowledge/linux.md"},e=l(`<h1 id="linux" tabindex="-1">Linux <a class="header-anchor" href="#linux" aria-hidden="true">#</a></h1><nav class="table-of-contents"><ul><li><a href="#\u5E38\u7528\u547D\u4EE4">\u5E38\u7528\u547D\u4EE4</a><ul><li><a href="#chmod">chmod</a></li><li><a href="#chown">chown</a></li><li><a href="#chgrp">chgrp</a></li></ul></li></ul></nav><h2 id="\u5E38\u7528\u547D\u4EE4" tabindex="-1">\u5E38\u7528\u547D\u4EE4 <a class="header-anchor" href="#\u5E38\u7528\u547D\u4EE4" aria-hidden="true">#</a></h2><h3 id="chmod" tabindex="-1">chmod <a class="header-anchor" href="#chmod" aria-hidden="true">#</a></h3><p>Linux chmod (\u82F1\u6587\u5168\u62FC: change mode)\u547D\u4EE4\u662F\u63A7\u5236\u7528\u6237\u5BF9\u6587\u4EF6\u7684\u6743\u9650\u7684\u547D\u4EE4</p><p>Linux/Unix \u7684\u6587\u4EF6\u8C03\u7528\u6743\u9650\u5206\u4E3A\u4E09\u7EA7 : \u6587\u4EF6\u6240\u6709\u8005 (Owner), \u7528\u6237\u7EC4 (Group), \u5176\u5B83\u7528\u6237 (Other Users)</p><h3 id="chown" tabindex="-1">chown <a class="header-anchor" href="#chown" aria-hidden="true">#</a></h3><p>Linux chown (\u82F1\u6587\u5168\u62FC: change owner)\u547D\u4EE4\u7528\u4E8E\u8BBE\u7F6E\u6587\u4EF6\u6240\u6709\u8005\u548C\u6587\u4EF6\u5173\u8054\u7EC4\u7684\u547D\u4EE4</p><div class="language-shell"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#676E95;"># \u628A /var/run/httpd.pid \u7684\u6240\u6709\u8005\u8BBE\u7F6E root: </span></span>
<span class="line"><span style="color:#A6ACCD;">chown root /var/run/httpd.pid</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;"># \u5C06\u6587\u4EF6 file1.txt \u7684\u62E5\u6709\u8005\u8BBE\u4E3A runoob, \u7FA4\u4F53\u7684\u4F7F\u7528\u8005 runoobgroup:</span></span>
<span class="line"><span style="color:#A6ACCD;">chown runoob:runoobgroup file1.txt</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;"># \u5C06\u5F53\u524D\u524D\u76EE\u5F55\u4E0B\u7684\u6240\u6709\u6587\u4EF6\u4E0E\u5B50\u76EE\u5F55\u7684\u62E5\u6709\u8005\u7686\u8BBE\u4E3A runoob, \u7FA4\u4F53\u7684\u4F7F\u7528\u8005 runoobgroup:</span></span>
<span class="line"><span style="color:#A6ACCD;">chown -R runoob:runoobgroup </span><span style="color:#89DDFF;">*</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;"># \u628A /home/runoob \u7684\u5173\u8054\u7EC4\u8BBE\u7F6E\u4E3A 512 (\u5173\u8054\u7EC4ID), \u4E0D\u6539\u53D8\u6240\u6709\u8005: </span></span>
<span class="line"><span style="color:#A6ACCD;">chown :512 /home/runoob</span></span>
<span class="line"></span></code></pre></div><h3 id="chgrp" tabindex="-1">chgrp <a class="header-anchor" href="#chgrp" aria-hidden="true">#</a></h3><p>Linux chgrp (\u82F1\u6587\u5168\u62FC: change group)\u547D\u4EE4\u7528\u4E8E\u53D8\u66F4\u6587\u4EF6\u6216\u76EE\u5F55\u7684\u6240\u5C5E\u7FA4\u7EC4</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">[root@localhost test]# ll</span></span>
<span class="line"><span style="color:#A6ACCD;">---xrw-r-- 1 root root 302108 11-13 06:03 log2012.log</span></span>
<span class="line"><span style="color:#A6ACCD;">[root@localhost test]# chgrp -v bin log2012.log</span></span>
<span class="line"><span style="color:#A6ACCD;">&quot;log2012.log&quot; \u7684\u6240\u5C5E\u7EC4\u5DF2\u66F4\u6539\u4E3A bin</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">[root@localhost test]# ll</span></span>
<span class="line"><span style="color:#A6ACCD;">---xrw-r-- 1 root bin  302108 11-13 06:03 log2012.log</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div>`,12),p=[e];function c(r,t,i,h,d,u){return a(),s("div",null,p)}const _=n(o,[["render",c]]);export{A as __pageData,_ as default};
