import{_ as a,c as e,o as i,a as l}from"./app.94f92628.js";const b=JSON.parse('{"title":"Middware","description":"","frontmatter":{},"headers":[{"level":2,"title":"RabbitMQ","slug":"rabbitmq"},{"level":3,"title":"\u7279\u70B9","slug":"\u7279\u70B9"},{"level":3,"title":"\u6D88\u606F\u751F\u4EA7\u6D88\u8D39","slug":"\u6D88\u606F\u751F\u4EA7\u6D88\u8D39"},{"level":3,"title":"\u7EC4\u6210","slug":"\u7EC4\u6210"},{"level":3,"title":"Exchange\u7684\u56DB\u79CD\u7C7B\u578B","slug":"exchange\u7684\u56DB\u79CD\u7C7B\u578B"},{"level":2,"title":"Kafka","slug":"kafka"},{"level":3,"title":"\u7279\u70B9","slug":"\u7279\u70B9-1"},{"level":3,"title":"\u6982\u5FF5","slug":"\u6982\u5FF5"},{"level":3,"title":"API","slug":"api"}],"relativePath":"knowledge/middleware.md","lastUpdated":1665546130000}'),t={name:"knowledge/middleware.md"},r=l('<h1 id="middware" tabindex="-1">Middware <a class="header-anchor" href="#middware" aria-hidden="true">#</a></h1><nav class="table-of-contents"><ul><li><a href="#rabbitmq">RabbitMQ</a><ul><li><a href="#\u7279\u70B9">\u7279\u70B9</a></li><li><a href="#\u6D88\u606F\u751F\u4EA7\u6D88\u8D39">\u6D88\u606F\u751F\u4EA7\u6D88\u8D39</a></li><li><a href="#\u7EC4\u6210">\u7EC4\u6210</a></li><li><a href="#exchange\u7684\u56DB\u79CD\u7C7B\u578B">Exchange\u7684\u56DB\u79CD\u7C7B\u578B</a></li></ul></li><li><a href="#kafka">Kafka</a><ul><li><a href="#\u7279\u70B9-1">\u7279\u70B9</a></li><li><a href="#\u6982\u5FF5">\u6982\u5FF5</a></li><li><a href="#api">API</a></li></ul></li></ul></nav><h2 id="rabbitmq" tabindex="-1">RabbitMQ <a class="header-anchor" href="#rabbitmq" aria-hidden="true">#</a></h2><h3 id="\u7279\u70B9" tabindex="-1">\u7279\u70B9 <a class="header-anchor" href="#\u7279\u70B9" aria-hidden="true">#</a></h3><p>RabbitMQ\u662F\u4E00\u6B3E\u4F7F\u7528Erlang\u8BED\u8A00\u5F00\u53D1\u7684, \u5B9E\u73B0AMQP(\u9AD8\u7EA7\u6D88\u606F\u961F\u5217\u534F\u8BAE)\u7684\u5F00\u6E90\u6D88\u606F\u4E2D\u95F4\u4EF6.</p><ul><li>\u53EF\u9760\u6027 - \u652F\u6301\u6301\u4E45\u5316, \u4F20\u8F93\u786E\u8BA4, \u53D1\u5E03\u786E\u8BA4\u7B49\u4FDD\u8BC1\u4E86MQ\u7684\u53EF\u9760\u6027</li><li>\u7075\u6D3B\u7684\u5206\u53D1\u6D88\u606F\u7B56\u7565 - \u8FD9\u5E94\u8BE5\u662FRabbitMQ\u7684\u4E00\u5927\u7279\u70B9. \u5728\u6D88\u606F\u8FDB\u5165MQ\u524D\u7531Exchange(\u4EA4\u6362\u673A)\u8FDB\u884C\u8DEF\u7531\u6D88\u606F. \u5206\u53D1\u6D88\u606F\u7B56\u7565\u6709: \u7B80\u5355\u6A21\u5F0F, \u5DE5\u4F5C\u961F\u5217\u6A21\u5F0F, \u53D1\u5E03\u8BA2\u9605\u6A21\u5F0F, \u8DEF\u7531\u6A21\u5F0F, \u901A\u914D\u7B26\u6A21\u5F0F</li><li>\u652F\u6301\u96C6\u7FA4 - \u591A\u53F0RabbitMQ\u670D\u52A1\u5668\u53EF\u4EE5\u7EC4\u6210\u4E00\u4E2A\u96C6\u7FA4, \u5F62\u6210\u4E00\u4E2A\u903B\u8F91Broker</li><li>\u591A\u79CD\u534F\u8BAE - RabbitMQ\u652F\u6301\u591A\u79CD\u6D88\u606F\u961F\u5217\u534F\u8BAE, \u6BD4\u5982 STOMP, MQTT \u7B49\u7B49</li><li>\u652F\u6301\u591A\u79CD\u8BED\u8A00\u5BA2\u6237\u7AEF - RabbitMQ\u51E0\u4E4E\u652F\u6301\u6240\u6709\u5E38\u7528\u7F16\u7A0B\u8BED\u8A00, \u5305\u62EC Java, .NET, Ruby \u7B49\u7B49</li><li>\u53EF\u89C6\u5316\u7BA1\u7406\u754C\u9762 - RabbitMQ\u63D0\u4F9B\u4E86\u4E00\u4E2A\u6613\u7528\u7684\u7528\u6237\u754C\u9762, \u4F7F\u5F97\u7528\u6237\u53EF\u4EE5\u76D1\u63A7\u548C\u7BA1\u7406\u6D88\u606F Broker</li><li>\u63D2\u4EF6\u673A\u5236 - RabbitMQ\u63D0\u4F9B\u4E86\u8BB8\u591A\u63D2\u4EF6, \u53EF\u4EE5\u901A\u8FC7\u63D2\u4EF6\u8FDB\u884C\u6269\u5C55, \u4E5F\u53EF\u4EE5\u7F16\u5199\u81EA\u5DF1\u7684\u63D2\u4EF6</li></ul><h3 id="\u6D88\u606F\u751F\u4EA7\u6D88\u8D39" tabindex="-1">\u6D88\u606F\u751F\u4EA7\u6D88\u8D39 <a class="header-anchor" href="#\u6D88\u606F\u751F\u4EA7\u6D88\u8D39" aria-hidden="true">#</a></h3><ul><li>\u751F\u4EA7\u8005client\u7AEF\u6307\u5B9A\u670D\u52A1\u7AEF\u5730\u5740\u4EE5\u53CAvhost\u865A\u62DF\u4E3B\u673A, \u8FDE\u63A5\u670D\u52A1\u7AEF</li><li>\u5EFA\u7ACB\u8FDE\u63A5\u540E, \u521B\u5EFAChannel\u4FE1\u9053, \u8BBE\u7F6E\u6B64\u4FE1\u9053\u7684\u5C5E\u6027</li><li>\u901A\u8FC7\u4FE1\u9053, \u58F0\u660E\u4EA4\u6362\u673A, \u961F\u5217, \u7ED1\u5B9A\u5173\u7CFB, \u4EE5\u53CA\u76F8\u5173\u5C5E\u6027</li><li>\u901A\u8FC7\u4FE1\u9053, \u53D1\u9001Message\u6D88\u606F\u5230Exchange\u4EA4\u6362\u673A</li><li>\u4EA4\u6362\u673A\u901A\u8FC7binding\u7ED1\u5B9A\u5173\u7CFB\u4E0ERoutingKey\u8DEF\u7531\u952E, \u5C06\u6D88\u606F\u5206\u53D1\u5230\u5BF9\u5E94\u7684Queue\u961F\u5217</li><li>\u6D88\u8D39\u8005client\u901A\u8FC7\u670D\u52A1\u7AEF\u5730\u5740\u4EE5\u53CAvhost\u8FDE\u63A5\u670D\u52A1\u7AEF</li><li>\u5EFA\u7ACB\u8FDE\u63A5\u540E, \u521B\u5EFA\u4FE1\u9053, \u8BBE\u7F6E\u4FE1\u9053\u5C5E\u6027</li><li>\u901A\u8FC7\u4FE1\u9053, \u5F00\u542F\u6D88\u8D39\u8005\u76D1\u542C\u961F\u5217, \u4ECE\u961F\u5217\u4E2D\u83B7\u53D6\u6D88\u606F\u8FDB\u884C\u6D88\u8D39</li><li>\u6839\u636E\u662F\u5426\u5EF6\u8FDF\u786E\u8BA4, \u786E\u8BA4\u6D88\u606F\u5DF2\u7ECF\u88AB\u6B63\u5E38\u6D88\u8D39\u6210\u529F\u540E, \u6D88\u606F\u4ECE\u961F\u5217\u4E2D\u5220\u9664</li></ul><h3 id="\u7EC4\u6210" tabindex="-1">\u7EC4\u6210 <a class="header-anchor" href="#\u7EC4\u6210" aria-hidden="true">#</a></h3><p><strong>Broker</strong>: \u6D88\u606F\u961F\u5217\u670D\u52A1\u8FDB\u7A0B. \u6B64\u8FDB\u7A0B\u5305\u62EC\u4E24\u4E2A\u90E8\u5206: Exchange\u548CQueue</p><p><strong>Exchange</strong>: \u6D88\u606F\u961F\u5217\u4EA4\u6362\u673A. \u6309\u4E00\u5B9A\u7684\u89C4\u5219\u5C06\u6D88\u606F\u8DEF\u7531\u8F6C\u53D1\u5230\u67D0\u4E2A\u961F\u5217</p><p><strong>Queue</strong>: \u6D88\u606F\u961F\u5217, \u5B58\u50A8\u6D88\u606F\u7684\u961F\u5217</p><p><strong>Producer</strong>: \u6D88\u606F\u751F\u4EA7\u8005. \u751F\u4EA7\u65B9\u5BA2\u6237\u7AEF\u5C06\u6D88\u606F\u540C\u4EA4\u6362\u673A\u8DEF\u7531\u53D1\u9001\u5230\u961F\u5217\u4E2D</p><p><strong>Consumer</strong>: \u6D88\u606F\u6D88\u8D39\u8005. \u6D88\u8D39\u961F\u5217\u4E2D\u5B58\u50A8\u7684\u6D88\u606F</p><h3 id="exchange\u7684\u56DB\u79CD\u7C7B\u578B" tabindex="-1">Exchange\u7684\u56DB\u79CD\u7C7B\u578B <a class="header-anchor" href="#exchange\u7684\u56DB\u79CD\u7C7B\u578B" aria-hidden="true">#</a></h3><p><strong>Direct Exchange</strong></p><p>\u5B8C\u5168\u5339\u914D\u578B\u4EA4\u6362\u673A, \u901A\u8FC7RoutingKey\u8DEF\u7531\u952E\u5C06\u4EA4\u6362\u673A\u548C\u961F\u5217\u8FDB\u884C\u7ED1\u5B9A, \u6D88\u606F\u88AB\u53D1\u9001\u5230exchange\u65F6, \u9700\u8981\u6839\u636E\u6D88\u606F\u7684RoutingKey, \u6765\u8FDB\u884C\u5339\u914D, \u53EA\u5C06\u6D88\u606F\u53D1\u9001\u5230\u5B8C\u5168\u5339\u914D\u5230\u6B64RoutingKey\u7684\u961F\u5217</p><p><strong>Fanout exchange</strong></p><p>\u6247\u51FA\u7C7B\u578B\u4EA4\u6362\u673A, \u6B64\u79CD\u4EA4\u6362\u673A, \u4F1A\u5C06\u6D88\u606F\u5206\u53D1\u7ED9\u6240\u6709\u7ED1\u5B9A\u4E86\u6B64\u4EA4\u6362\u673A\u7684\u961F\u5217, \u6B64\u65F6RoutingKey\u53C2\u6570\u65E0\u6548. \u5F88\u50CF\u5B50\u7F51\u5E7F\u64AD, \u6BCF\u53F0\u5B50\u7F51\u5185\u7684\u4E3B\u673A\u90FD\u83B7\u5F97\u4E86\u4E00\u4EFD\u590D\u5236\u7684\u6D88\u606F. \u7B80\u5355\u70B9\u8BF4\u5C31\u662F\u53D1\u5E03\u8BA2\u9605</p><p><strong>Topic Exchange</strong></p><p>\u4E3B\u9898\u4EA4\u6362\u673A, \u5982\u679C\u4ECE\u7528\u6CD5\u4E0A\u9762\u7FFB\u8BD1\u53EF\u80FD\u53EB\u901A\u914D\u7B26\u4EA4\u6362\u673A\u4F1A\u66F4\u52A0\u8D34\u5207. \u8FD9\u79CD\u4EA4\u6362\u673A\u662F\u4F7F\u7528\u901A\u914D\u7B26\u53BB\u5339\u914D, \u8DEF\u7531\u5230\u5BF9\u5E94\u7684\u961F\u5217. \u901A\u914D\u7B26\u6709\u4E24\u79CD: &quot;*&quot; , &quot;#&quot;. \u9700\u8981\u6CE8\u610F\u7684\u662F\u901A\u914D\u7B26\u524D\u9762\u5FC5\u987B\u8981\u52A0\u4E0A&quot;.&quot;\u7B26\u53F7</p><p>* \u7B26\u53F7: \u6709\u4E14\u53EA\u5339\u914D\u4E00\u4E2A\u8BCD. \u6BD4\u5982 a.*\u53EF\u4EE5\u5339\u914D\u5230&quot;a.b&quot;, &quot;a.c&quot;, \u4F46\u662F\u5339\u914D\u4E0D\u4E86&quot;a.b.c&quot;</p><p># \u7B26\u53F7: \u5339\u914D\u4E00\u4E2A\u6216\u591A\u4E2A\u8BCD. \u6BD4\u5982&quot;rabbit.#&quot;\u65E2\u53EF\u4EE5\u5339\u914D\u5230&quot;rabbit.a.b&quot;, &quot;rabbit.a&quot;, \u4E5F\u53EF\u4EE5\u5339\u914D\u5230&quot;rabbit.a.b.c&quot;</p><p><strong>Headers Exchange</strong></p><p>headers\u4FE1\u606F\u7C7B\u578B\u4EA4\u6362\u673A, \u6B64\u7C7B\u578B\u4EA4\u6362\u673A\u4E0D\u901A\u8FC7routingkey\u8DEF\u7531\u952E\u6765\u5206\u53D1\u6D88\u606F, \u800C\u662F\u901A\u8FC7\u6D88\u606F\u5185\u5BB9\u4E2D\u7684headers\u5C5E\u6027\u6765\u8FDB\u884C\u5339\u914D. headers\u7C7B\u578B\u4EA4\u6362\u5668\u6027\u80FD\u5DEE, \u5728\u5B9E\u9645\u4E2D\u5E76\u4E0D\u5E38\u7528</p><h2 id="kafka" tabindex="-1">Kafka <a class="header-anchor" href="#kafka" aria-hidden="true">#</a></h2><p>Kafka\u662FApache\u65D7\u4E0B\u7684\u4E00\u6B3E\u5206\u5E03\u5F0F\u6D41\u5A92\u4F53\u5E73\u53F0, Kafka\u662F\u4E00\u79CD\u9AD8\u541E\u5410\u91CF, \u6301\u4E45\u6027, \u5206\u5E03\u5F0F\u7684\u53D1\u5E03\u8BA2\u9605\u7684\u6D88\u606F\u961F\u5217\u7CFB\u7EDF</p><h3 id="\u7279\u70B9-1" tabindex="-1">\u7279\u70B9 <a class="header-anchor" href="#\u7279\u70B9-1" aria-hidden="true">#</a></h3><ul><li><p>\u9AD8\u541E\u5410\u91CF: \u53EF\u4EE5\u6EE1\u8DB3\u6BCF\u79D2\u767E\u4E07\u7EA7\u522B\u6D88\u606F\u7684\u751F\u4EA7\u548C\u6D88\u8D39</p></li><li><p>\u6301\u4E45\u6027: \u6709\u4E00\u5957\u5B8C\u5584\u7684\u6D88\u606F\u5B58\u50A8\u673A\u5236, \u786E\u4FDD\u6570\u636E\u9AD8\u6548\u5B89\u5168\u4E14\u6301\u4E45\u5316</p></li><li><p>\u5206\u5E03\u5F0F: \u57FA\u4E8E\u5206\u5E03\u5F0F\u7684\u6269\u5C55; Kafka\u7684\u6570\u636E\u90FD\u4F1A\u590D\u5236\u5230\u51E0\u53F0\u670D\u52A1\u5668\u4E0A, \u5F53\u67D0\u53F0\u6545\u969C\u5931\u6548\u65F6, \u751F\u4EA7\u8005\u548C\u6D88\u8D39\u8005\u8F6C\u800C\u4F7F\u7528\u5176\u5B83\u7684Kafka</p></li></ul><h3 id="\u6982\u5FF5" tabindex="-1">\u6982\u5FF5 <a class="header-anchor" href="#\u6982\u5FF5" aria-hidden="true">#</a></h3><p>Kafka\u4F5C\u4E3A\u4E00\u4E2A\u96C6\u7FA4\u8FD0\u884C\u5728\u4E00\u4E2A\u6216\u591A\u4E2A\u670D\u52A1\u5668\u4E0A, \u670D\u52A1\u5668\u53EF\u4EE5\u8DE8\u591A\u4E2A\u673A\u623F, \u662F\u5206\u5E03\u5F0F\u7684\u53D1\u5E03\u8BA2\u9605\u6D88\u606F\u961F\u5217\u7CFB\u7EDF</p><p>Kafka\u96C6\u7FA4\u5C06\u8BB0\u5F55\u6D41\u5B58\u50A8\u5728\u79F0\u4E3ATopic\u7684\u7C7B\u522B\u4E2D</p><p>\u6BCF\u6761\u8BB0\u5F55\u7531\u952E\u503C (key, value) \u548C\u4E00\u4E2A\u65F6\u95F4\u6233\u7EC4\u6210</p><h3 id="api" tabindex="-1">API <a class="header-anchor" href="#api" aria-hidden="true">#</a></h3><ul><li>Producer API: \u751F\u4EA7\u8005API\u5141\u8BB8\u5E94\u7528\u7A0B\u5E8F\u5C06\u4E00\u7EC4\u8BB0\u5F55\u53D1\u5E03\u5230\u4E00\u4E2A\u6216\u591A\u4E2AKafka Topic\u4E2D</li><li>Consumer API: \u6D88\u8D39\u8005API\u5141\u8BB8\u5E94\u7528\u7A0B\u5E8F\u8BA2\u9605\u4E00\u4E2A\u6216\u591A\u4E2ATopic, \u5E76\u5904\u7406\u5411\u4ED6\u4EEC\u4F20\u8F93\u7684\u8BB0\u5F55\u6D41</li><li>Streams API: \u6D41API\u5141\u8BB8\u5E94\u7528\u7A0B\u5E8F\u5145\u5F53\u6D41\u5904\u7406\u5668, \u4ECE\u4E00\u4E2A\u6216\u8005\u591A\u4E2ATopic\u4E2D\u6D88\u8D39\u8F93\u5165\u6D41, \u5E76\u5C06\u8F93\u51FA\u6D41\u751F\u6210\u4E3A\u4E00\u4E2A\u6216\u591A\u4E2A\u8F93\u51FA\u4E3B\u9898, \u4ECE\u800C\u5C06\u8F93\u5165\u6D41\u6709\u6548\u5730\u8F6C\u6362\u4E3A\u8F93\u51FA\u6D41</li><li>Connector API: \u8FDE\u63A5\u5668API\u5141\u8BB8\u6784\u5EFA\u548C\u8FD0\u884C\u53EF\u91CD\u7528\u7684\u751F\u4EA7\u8005\u6216\u6D88\u8D39\u8005, \u8FD9\u4E9B\u751F\u4EA7\u8005\u6216\u6D88\u8D39\u8005\u5C06Kafka Topic\u8FDE\u63A5\u5230\u73B0\u6709\u7684\u5E94\u7528\u7A0B\u5E8F\u6216\u6570\u636E\u7CFB\u7EDF. \u4F8B\u5982: \u8FDE\u63A5\u5230\u5173\u7CFB\u6570\u636E\u5E93\u7684\u8FDE\u63A5\u5668\u53EF\u80FD\u4F1A\u6355\u83B7\u5BF9\u8868\u7684\u6BCF\u6B21\u66F4\u6539.</li></ul>',35),n=[r];function o(h,d,s,u,c,p){return i(),e("div",null,n)}const f=a(t,[["render",o]]);export{b as __pageData,f as default};
