import{_ as a,c as l,o as e,a as o}from"./app.54f4c473.js";const f=JSON.parse('{"title":"Python Web Framework","description":"","frontmatter":{},"headers":[{"level":2,"title":"Web Framework Compare","slug":"web-framework-compare"},{"level":3,"title":"Django","slug":"django"},{"level":3,"title":"Flask","slug":"flask"},{"level":3,"title":"Tornado","slug":"tornado"},{"level":3,"title":"FastAPI","slug":"fastapi"},{"level":2,"title":"Django","slug":"django-1"},{"level":2,"title":"Flask","slug":"flask-1"},{"level":2,"title":"Tornado","slug":"tornado-1"},{"level":2,"title":"FastAPI","slug":"fastapi-1"}],"relativePath":"knowledge/python-web-framework.md","lastUpdated":1661332350000}'),i={name:"knowledge/python-web-framework.md"},r=o('<h1 id="python-web-framework" tabindex="-1">Python Web Framework <a class="header-anchor" href="#python-web-framework" aria-hidden="true">#</a></h1><nav class="table-of-contents"><ul><li><a href="#web-framework-compare">Web Framework Compare</a><ul><li><a href="#django">Django</a></li><li><a href="#flask">Flask</a></li><li><a href="#tornado">Tornado</a></li><li><a href="#fastapi">FastAPI</a></li></ul></li><li><a href="#django-1">Django</a></li><li><a href="#flask-1">Flask</a></li><li><a href="#tornado-1">Tornado</a></li><li><a href="#fastapi-1">FastAPI</a></li></ul></nav><h2 id="web-framework-compare" tabindex="-1">Web Framework Compare <a class="header-anchor" href="#web-framework-compare" aria-hidden="true">#</a></h2><h3 id="django" tabindex="-1">Django <a class="header-anchor" href="#django" aria-hidden="true">#</a></h3><p>Django \u662F\u7528\u4E8E\u6784\u5EFA\u7F51\u7AD9\u7684\u514D\u8D39\u5F00\u6E90 Web \u5F00\u53D1\u6846\u67B6. \u5B83\u4E8E 2003 \u5E74\u521B\u5EFA, \u5E76\u4F7F\u7528 MTV \u6A21\u5F0F, \u4E5F\u5C31\u662F Model-Template-View \u6A21\u5F0F, \u5982\u679C\u4E0D\u4F7F\u7528 Django \u7684\u6A21\u7248\u8BED\u6CD5, \u4E5F\u53EF\u4EE5\u8BA4\u4E3A\u662F MVC \u6A21\u5F0F, \u5373 Model-View-Controler, \u5BF9\u5E94\u4E09\u4E2A\u6587\u4EF6: <code>models.py</code>, <code>views.py</code>, <code>urls.py</code></p><p><strong>\u4F18\u70B9</strong></p><ul><li>Django \u6846\u67B6\u7684\u4EE3\u7801\u7ED3\u6784\u7EDF\u4E00\u4E14\u6E05\u6670\u6613\u61C2, \u5F00\u53D1\u4EBA\u5458\u53EF\u4EE5\u50CF\u505A\u586B\u7A7A\u9898\u4E00\u6837\u6DFB\u52A0\u66F4\u591A\u529F\u80FD, \u4E5F\u53EF\u4EE5\u8BBE\u8BA1\u81EA\u5B9A\u4E49\u7684\u7C7B\u6216\u63A5\u53E3, \u5373\u7B80\u5355\u4E5F\u4E0D\u5931\u7075\u6D3B\u6027, \u6269\u5C55\u6027</li><li>Django \u751F\u6001\u8FD8\u6709\u4E00\u4E2A\u5229\u5668\u5C31\u662F Django Rest Framework (DRF) , DRF \u53EF\u4EE5\u8F7B\u677E\u642D\u5EFA\u5177\u6709 REST \u98CE\u683C\u7684 API, \u7531\u4E8E\u5B83\u5177\u6709\u6A21\u5757\u5316\u548C\u53EF\u81EA\u5B9A\u4E49\u7684\u67B6\u6784, \u5728\u524D\u540E\u7AEF\u5206\u79BB\u7684\u8D8B\u52BF\u4E0B, \u4F7F\u7528 Django \u6784\u5EFA Web API \u4E5F\u975E\u5E38\u5BB9\u6613</li><li>\u81EA\u5E26\u5B89\u5168\u6027\u7684\u4E2D\u95F4\u4EF6. Django \u901A\u8FC7\u63D0\u4F9B\u9488\u5BF9\u8054\u5408 SQL \u6CE8\u5165\u548C\u8DE8\u7AD9\u70B9\u8BF7\u6C42\u4F2A\u9020\u653B\u51FB\u7684\u9632\u5FA1\u6765\u63D0\u4F9B\u5B89\u5168\u6027\u7684\u8BBF\u95EE</li><li>Django \u751F\u6001\u975E\u5E38\u4E30\u5BCC, \u7B2C\u4E09\u65B9\u7C7B\u9002\u914D\u7684\u7C7B\u5E93\u4E5F\u975E\u5E38\u591A</li></ul><p><strong>\u7F3A\u70B9</strong></p><ul><li>Django Models \u6CA1\u6709 mixins, \u53EA\u6709\u7B80\u5355\u7684\u7EE7\u627F</li><li>Django \u4F7F\u7528\u7684 ORM \u662F\u5728 SQLAlchemy \u4E4B\u524D\u521B\u5EFA\u7684, \u56E0\u6B64 Django ORM \u7684\u7075\u6D3B\u6027\u8F83\u5DEE, \u56E0\u6B64\u5B83\u4E0D\u5982 SQLAlchemy</li></ul><p><strong>\u7528\u4F8B</strong></p><p>Django \u53EF\u4EE5\u5904\u7406\u4EFB\u4F55\u89C4\u6A21\u548C\u5BB9\u91CF\u7684\u9879\u76EE. \u53EF\u4EE5\u5C06\u5176\u7528\u4E8E\u7B80\u5355\u7AD9\u70B9\u6216\u9AD8\u6027\u80FD\u7AD9\u70B9. \u4E00\u4E9B\u7528\u4F8B\u5982\u4E0B:</p><ul><li>\u9AD8\u8D1F\u8377\u9884\u8BA2\u5F15\u64CE</li><li>\u8D2D\u7269\u5E73\u53F0</li><li>\u5B66\u6821\u7BA1\u7406\u7CFB\u7EDF</li><li>\u5185\u7F6E\u7528\u4E8E\u5185\u90E8\u6570\u636E\u7684\u81EA\u5B9A\u4E49 CRM \u7CFB\u7EDF</li><li>\u652F\u6301\u57FA\u4E8E Web \u7684\u5E94\u7528\u7A0B\u5E8F\u7684 IOS \u548C Android \u5E94\u7528\u7A0B\u5E8F</li><li>\u7BA1\u7406\u4EEA\u8868\u677F, \u57FA\u4E8E\u7167\u7247\u7684\u9A8C\u8BC1, \u7528\u4E8E\u53D1\u9001\u7528\u6237\u901A\u77E5\u7684\u7535\u5B50\u90AE\u4EF6\u7CFB\u7EDF\u7B49</li></ul><p>\u7B80\u800C\u8A00\u4E4B, \u5982\u679C\u4F60\u8981\u505A\u4E00\u4E2A\u4E0A\u89C4\u6A21, \u4E0D\u65AD\u6269\u5C55\u7684, \u5927\u89C4\u6A21, \u5546\u4E1A\u5316\u7684 Web \u9879\u76EE, Django \u662F\u4F60\u6700\u597D\u7684\u9009\u62E9</p><h3 id="flask" tabindex="-1">Flask <a class="header-anchor" href="#flask" aria-hidden="true">#</a></h3><p>Flask \u662F\u4E00\u4E2A\u7528 Python \u7F16\u5199\u7684\u5FAE\u578B Web \u6846\u67B6, \u53EF\u7528\u4E8E\u5F00\u53D1\u7B80\u7EA6\u7684 Web \u5E94\u7528\u7A0B\u5E8F. Flask \u5E26\u6709\u8BF8\u5982\u6A21\u677F\u5F15\u64CE, \u7F13\u5B58\u548C\u8EAB\u4EFD\u9A8C\u8BC1\u4E4B\u7C7B\u7684\u9009\u9879</p><p>\u5B83\u7684\u521B\u5EFA\u662F\u4E3A\u4E86\u4F7F\u7528 Python \u7F16\u7A0B\u8BED\u8A00\u6784\u5EFA Web \u5E94\u7528\u7A0B\u5E8F. \u5B83\u88AB\u8BBE\u8BA1\u4E3A\u6613\u7528, \u5FEB\u901F\u5E76\u4E14\u53EF\u4EE5\u6269\u5C55\u6210\u4E3A\u590D\u6742\u7684\u5E94\u7528\u7A0B\u5E8F\u6216\u5FAE\u670D\u52A1</p><p>Flask \u66F4\u50CF\u662F\u521B\u4F5C\u8005 Armin Ronacher \u5728 4 \u6708 1 \u65E5\u5BF9\u611A\u4EBA\u8282\u7684\u6076\u4F5C\u5267, \u5728\u5176\u4E2D\u4F9D\u8D56\u4E86Werkzeug \u548C Jinja. \u4EE4\u4EBA\u60CA\u8BB6\u7684\u662F, \u5B83\u53D7\u5230\u4E86\u793E\u533A\u7684\u8D5E\u8D4F, \u8FD9\u4F7F\u4ED6\u4EEC\u5728\u6B64\u65B9\u9762\u505A\u5F97\u66F4\u591A</p><p><strong>\u4F18\u70B9</strong></p><ul><li>\u7BA1\u7406\u4EEA\u8868\u677F, \u57FA\u4E8E\u7167\u7247\u7684\u9A8C\u8BC1, \u7528\u4E8E\u53D1\u9001\u7528\u6237\u901A\u77E5\u7684\u7535\u5B50\u90AE\u4EF6\u7CFB\u7EDF\u7B49</li><li>Flask \u5141\u8BB8\u8FDB\u884C\u5355\u5143\u6D4B\u8BD5, \u5E76\u4E14\u7531\u4E8E\u5176\u5185\u7F6E\u7684\u5F00\u53D1\u670D\u52A1\u5668, \u96C6\u6210\u7684\u652F\u6301\u7B49, \u56E0\u6B64\u53EF\u4EE5\u901A\u8FC7\u5BF9\u4E00\u4E9B\u6269\u5C55\u8FDB\u884C\u8C03\u6574\u6765\u8FC7\u6E21\u5230 Web \u6846\u67B6</li><li>Flask \u7B80\u5355\u6613\u7528, \u975E\u5E38\u9002\u5408\u521D\u5B66\u8005\u4F7F\u7528, \u4E3A\u5F00\u53D1\u4EBA\u5458\u63D0\u4F9B\u4E86\u66F4\u597D\u5730\u5B66\u4E60\u548C\u7406\u89E3\u5B83\u7684\u7A7A\u95F4. \u5B83\u8FD8\u4F7F\u5F00\u53D1\u4EBA\u5458\u53EF\u4EE5\u6BEB\u4E0D\u8D39\u529B\u5730\u5FEB\u901F\u521B\u5EFA\u5E94\u7528\u7A0B\u5E8F</li></ul><p><strong>\u7F3A\u70B9</strong></p><ul><li>Flask \u7684\u5F88\u591A\u6A21\u5757\u7531\u7B2C\u4E09\u65B9\u5F00\u53D1, \u5BB9\u6613\u5F15\u8D77\u5B89\u5168\u6F0F\u6D1E</li><li>Flask \u5177\u6709\u4E00\u4E2A\u5355\u4E00\u7684\u6765\u6E90, \u8868\u793A\u5B83\u5C06\u4F9D\u6B21\u5904\u7406\u6BCF\u4E2A\u8BF7\u6C42, \u56E0\u6B64, \u65E0\u8BBA\u6709\u591A\u5C11\u4E2A\u8BF7\u6C42, \u5B83\u4ECD\u7136\u4F1A\u8F6E\u6D41\u5904\u7406\u5B83\u4EEC, \u8FD9\u4F1A\u8017\u8D39\u66F4\u591A\u65F6\u95F4</li><li>\u5982\u679C\u5F00\u53D1\u4EBA\u5458\u6C34\u5E73\u4E0D\u9AD8, \u66F4\u5BB9\u6613\u4F7F\u7528\u4F4E\u8D28\u91CF\u7684\u4EE3\u7801\u521B\u5EFA\u4E00\u4E2A\u4E0D\u826F\u7684 Web \u5E94\u7528\u7A0B\u5E8F</li></ul><p><strong>\u7528\u4F8B</strong></p><p>\u53EF\u4EE5\u5C06 Flask \u7528\u4E8E\u5546\u4E1A\u9879\u76EE. \u5B83\u53EF\u4EE5\u5E2E\u52A9\u4F60\u5FEB\u901F\u5165\u95E8, \u4F46\u662F\u7F51\u7AD9\u9AD8\u8D1F\u8377\u60C5\u51B5\u4E0B\u6548\u679C\u4E0D\u4F73</p><p>\u53EF\u4EE5\u5FEB\u901F\u5B9E\u65BD Flask \u9879\u76EE, \u4F8B\u5982:</p><ul><li>\u7535\u5B50\u5546\u52A1\u7CFB\u7EDF</li><li>Facebook / Twitter\u673A\u5668\u4EBA</li><li>\u5728\u7EBF\u793E\u4EA4\u7F51\u7EDC</li><li>\u9759\u6001\u7F51\u7AD9</li></ul><p>\u5982\u679C\u4F60\u8981\u505A\u4E00\u4E9B\u5C0F\u578B\u4E2A\u4EBA\u9879\u76EE, \u6BD4\u5982\u804A\u5929\u673A\u5668\u4EBA, \u6216\u8005\u60F3\u5B9E\u73B0\u4EA7\u54C1\u7684\u5FEB\u901F\u539F\u578B, \u6216\u8005\u559C\u6B22\u81EA\u7531\u7684\u7F16\u5199\u4EE3\u7801\u63A7\u5236\u7A0B\u5E8F\u7684\u6D41\u7A0B, \u90A3\u4E48\u53EF\u4EE5\u9009\u62E9 Flask, \u5982\u679C\u8BF4 Django \u662F\u5177\u5907\u534A\u81EA\u52A8\u9A7E\u9A76\u7684\u5927\u578B\u8F66, \u90A3\u4E48 Flask \u5C31\u662F\u8BA9\u4F60\u81EA\u7531\u64CD\u7EB5\u7684\u624B\u52A8\u6321\u5C0F\u6C7D\u8F66</p><h3 id="tornado" tabindex="-1">Tornado <a class="header-anchor" href="#tornado" aria-hidden="true">#</a></h3><p>Tornado\u662FFacebook\u5F00\u6E90\u51FA\u6765\u7684\u6846\u67B6, \u5176\u54F2\u5B66\u8DDFDjango\u8FD1\u4E4E\u4E24\u4E2A\u6781\u7AEF</p><p>Tornado\u662F\u5F02\u6B65\u6846\u67B6Tornado\u57FA\u672C\u4E0A\u53EA\u7B97\u6709MVC\u4E2DC\u8FD9\u4E00\u5C42</p><p>\u8F7B\u91CF, \u5F02\u6B65\u975E\u963B\u585EIO\u5904\u7406\u65B9\u5F0F, \u51FA\u8272\u7684\u6297\u8D1F\u8F7D\u80FD\u529B, \u534F\u7A0B\u5E26\u6765\u4F18\u5F02\u7684\u5904\u7406\u6027\u80FD</p><p><strong>\u4F18\u70B9</strong></p><ul><li>\u5C11\u800C\u7CBE (\u8F7B\u91CF\u7EA7\u6846\u67B6)</li><li>\u6CE8\u91CD\u6027\u80FD\u4F18\u8D8A, \u901F\u5EA6\u5FEB</li><li>\u89E3\u51B3\u9AD8\u5E76\u53D1 (\u8BF7\u6C42\u5904\u7406\u662F\u57FA\u4E8E\u56DE\u8C03\u7684\u975E\u963B\u585E\u8C03\u7528)</li><li>\u5F02\u6B65\u975E\u963B\u585E</li><li>Websockets \u957F\u8FDE\u63A5</li><li>\u5185\u5D4C HTTP \u670D\u52A1\u5668</li><li>\u5355\u7EBF\u7A0B\u7684\u5F02\u6B65\u7F51\u7EDC\u7A0B\u5E8F, \u9ED8\u8BA4\u542F\u52A8\u65F6\u6839\u636ECPU\u6570\u91CF\u8FD0\u884C\u591A\u4E2A\u5B9E\u4F8B, \u5229\u7528CPU\u591A\u6838\u7684\u4F18\u52BF</li><li>\u81EA\u5B9A\u4E49\u6A21\u5757</li></ul><p><strong>\u7F3A\u70B9</strong></p><ul><li>\u6CA1\u6709ORM, \u63D0\u4F9B\u7684\u652F\u6301\u548C\u6A21\u677F\u5C11, \u7F3A\u5C11\u540E\u53F0\u652F\u6301, \u5BF9\u5C0F\u578B\u9879\u76EE\u6765\u8BF4\u5F00\u53D1\u901F\u5EA6\u6CA1\u6709Django\u5FEB</li><li>\u6A21\u677F\u548C\u6570\u636E\u5E93\u90E8\u5206\u6709\u5F88\u591A\u7B2C\u4E09\u65B9\u7684\u6A21\u5757\u53EF\u4F9B\u9009\u62E9, \u8FD9\u6837\u4E0D\u5229\u4E8E\u5C01\u88C5\u4E3A\u4E00\u4E2A\u529F\u80FD\u6A21\u5757</li></ul><p><strong>\u7528\u4F8B</strong></p><p>\u8981\u6027\u80FD, Tornado \u9996\u9009, Tornado \u9002\u5408\u9AD8\u5EA6\u5B9A\u5236, \u9002\u5408\u8BBF\u95EE\u91CF\u5927, \u5F02\u6B65\u60C5\u51B5\u591A\u7684\u7F51\u7AD9</p><h3 id="fastapi" tabindex="-1">FastAPI <a class="header-anchor" href="#fastapi" aria-hidden="true">#</a></h3><p>FastAPI \u662F\u7528\u4E8E\u6784\u5EFA Web API \u7684\u73B0\u4EE3, \u5F00\u6E90, \u5FEB\u901F, \u9AD8\u6027\u80FD\u7684 Web \u6846\u67B6, \u5B83\u57FA\u4E8EPython 3.6+ \u6807\u51C6\u7C7B\u578B\u63D0\u793A, \u652F\u6301\u5F02\u6B65, \u6B63\u5982\u5B83\u7684\u540D\u5B57, FastAPI \u5C31\u662F\u4E3A\u6784\u5EFA\u5FEB\u901F\u7684 API \u800C\u751F</p><p><strong>\u4F18\u70B9</strong></p><ul><li>\u81EA\u52A8\u7C7B\u578B\u68C0\u67E5. \u8FD9\u610F\u5473\u7740\u66F4\u5C11\u7684 Bug, \u5373\u4F7F\u5728\u6DF1\u5EA6\u5D4C\u5957\u7684 JSON \u8BF7\u6C42\u4E2D, Fast API \u4E5F\u4F1A\u9A8C\u8BC1\u5F00\u53D1\u4EBA\u5458\u7684\u6570\u636E\u7C7B\u578B</li><li>\u96C6\u4F17\u6240\u957F, \u7AD9\u5728\u5DE8\u4EBA\u7684\u80A9\u8180\u4E0A. FastAPI \u5EFA\u7ACB\u5728 JSON Schema (\u7528\u4E8E\u9A8C\u8BC1JSON\u6570\u636E\u7ED3\u6784\u7684\u5DE5\u5177) , OAuth 2.0 (\u7528\u4E8E\u6388\u6743\u7684\u884C\u4E1A\u6807\u51C6\u534F\u8BAE) \u548COpenAPI (\u8FD9\u662F\u53EF\u516C\u5F00\u83B7\u5F97\u7684\u5E94\u7528\u7A0B\u5E8F\u7F16\u7A0B\u63A5\u53E3) \u4E4B\u7C7B\u7684\u6807\u51C6\u4E4B\u4E0A</li><li>\u73B0\u4EE3\u5316. FastAPI \u4F7F\u4F7F\u7528\u79F0\u4E3A graphene-python \u7684 Python \u5E93\u8F7B\u677E\u6784\u5EFA GraphQL API</li><li>\u5FEB\u901F, \u9AD8\u6027\u80FD. \u53EF\u4EE5\u548C NodeJS \u548C Go \u76F8\u63D0\u5E76\u8BBA</li></ul><p><strong>\u7F3A\u70B9</strong></p><ul><li>FastAPI \u76F8\u5BF9\u8F83\u65B0, \u56E0\u6B64\u4E0E\u5176\u4ED6\u6846\u67B6\u76F8\u6BD4, \u793E\u533A\u8F83\u5C0F, \u7B2C\u4E09\u65B9\u7684\u6559\u7A0B\u76F8\u5BF9\u8F83\u5C11</li></ul><p><strong>\u7528\u4F8B</strong></p><ul><li>FastAPI \u9002\u7528\u4E8E\u6784\u5EFA\u9AD8\u6027\u80FD\u7684 API, \u672C\u8EAB\u652F\u6301\u5F02\u6B65, \u5982\u679C\u8981\u6784\u5EFA\u5F02\u6B65 API, \u53EF\u4EE5\u4F18\u5148\u9009\u62E9 FastAPI</li><li>Netflix \u5C06\u5176\u7528\u4E8E\u5185\u90E8\u5371\u673A\u7BA1\u7406</li><li>\u8FD8\u53EF\u4EE5\u5728\u90E8\u7F72\u51C6\u5907\u5C31\u7EEA\u7684\u673A\u5668\u5B66\u4E60\u6A21\u578B\u65F6\u5B8C\u7F8E\u7F29\u653E, \u56E0\u4E3A\u5F53 ML \u6A21\u578B\u5C01\u88C5\u5728 REST API \u5E76\u90E8\u7F72\u5728\u5FAE\u670D\u52A1\u4E2D\u65F6, \u5B83\u5728\u751F\u4EA7\u4E2D\u4F1A\u53D1\u6325\u6700\u4F73\u4F5C\u7528</li></ul><h2 id="django-1" tabindex="-1">Django <a class="header-anchor" href="#django-1" aria-hidden="true">#</a></h2><h2 id="flask-1" tabindex="-1">Flask <a class="header-anchor" href="#flask-1" aria-hidden="true">#</a></h2><h2 id="tornado-1" tabindex="-1">Tornado <a class="header-anchor" href="#tornado-1" aria-hidden="true">#</a></h2><h2 id="fastapi-1" tabindex="-1">FastAPI <a class="header-anchor" href="#fastapi-1" aria-hidden="true">#</a></h2>',48),t=[r];function n(s,d,h,p,g,c){return e(),l("div",null,t)}const k=a(i,[["render",n]]);export{f as __pageData,k as default};
