import{_ as e,c as a,o as i,a as r}from"./app.f2ad819c.js";const R=JSON.parse('{"title":"Redis","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u6570\u636E\u7C7B\u578B","slug":"\u6570\u636E\u7C7B\u578B"},{"level":2,"title":"\u5E95\u5C42\u6570\u636E\u7ED3\u6784","slug":"\u5E95\u5C42\u6570\u636E\u7ED3\u6784"},{"level":2,"title":"\u4E3A\u4EC0\u4E48\u5FEB","slug":"\u4E3A\u4EC0\u4E48\u5FEB"},{"level":2,"title":"\u5185\u5B58\u6DD8\u6C70","slug":"\u5185\u5B58\u6DD8\u6C70"},{"level":2,"title":"\u6301\u4E45\u5316","slug":"\u6301\u4E45\u5316"},{"level":2,"title":"\u4E8B\u52A1","slug":"\u4E8B\u52A1"},{"level":2,"title":"\u7F13\u5B58\u7A7F\u900F \u7F13\u5B58\u96EA\u5D29 \u7F13\u5B58\u51FB\u7A7F","slug":"\u7F13\u5B58\u7A7F\u900F-\u7F13\u5B58\u96EA\u5D29-\u7F13\u5B58\u51FB\u7A7F"},{"level":2,"title":"\u6D88\u606F\u4E22\u5931","slug":"\u6D88\u606F\u4E22\u5931"},{"level":2,"title":"\u66F4\u65B0\u7F13\u5B58\u7B56\u7565","slug":"\u66F4\u65B0\u7F13\u5B58\u7B56\u7565"},{"level":2,"title":"\u5982\u4F55\u89E3\u51B3Redis\u7684\u5E76\u53D1\u7ADE\u4E89key\u95EE\u9898","slug":"\u5982\u4F55\u89E3\u51B3redis\u7684\u5E76\u53D1\u7ADE\u4E89key\u95EE\u9898"},{"level":2,"title":"Redis\u548CMemcached\u7684\u533A\u522B","slug":"redis\u548Cmemcached\u7684\u533A\u522B"}],"relativePath":"knowledge/redis.md","lastUpdated":1666506634000}'),t={name:"knowledge/redis.md"},l=r('<h1 id="redis" tabindex="-1">Redis <a class="header-anchor" href="#redis" aria-hidden="true">#</a></h1><nav class="table-of-contents"><ul><li><a href="#\u6570\u636E\u7C7B\u578B">\u6570\u636E\u7C7B\u578B</a></li><li><a href="#\u5E95\u5C42\u6570\u636E\u7ED3\u6784">\u5E95\u5C42\u6570\u636E\u7ED3\u6784</a></li><li><a href="#\u4E3A\u4EC0\u4E48\u5FEB">\u4E3A\u4EC0\u4E48\u5FEB</a></li><li><a href="#\u5185\u5B58\u6DD8\u6C70">\u5185\u5B58\u6DD8\u6C70</a></li><li><a href="#\u6301\u4E45\u5316">\u6301\u4E45\u5316</a></li><li><a href="#\u4E8B\u52A1">\u4E8B\u52A1</a></li><li><a href="#\u7F13\u5B58\u7A7F\u900F-\u7F13\u5B58\u96EA\u5D29-\u7F13\u5B58\u51FB\u7A7F">\u7F13\u5B58\u7A7F\u900F \u7F13\u5B58\u96EA\u5D29 \u7F13\u5B58\u51FB\u7A7F</a></li><li><a href="#\u6D88\u606F\u4E22\u5931">\u6D88\u606F\u4E22\u5931</a></li><li><a href="#\u66F4\u65B0\u7F13\u5B58\u7B56\u7565">\u66F4\u65B0\u7F13\u5B58\u7B56\u7565</a></li><li><a href="#\u5982\u4F55\u89E3\u51B3redis\u7684\u5E76\u53D1\u7ADE\u4E89key\u95EE\u9898">\u5982\u4F55\u89E3\u51B3Redis\u7684\u5E76\u53D1\u7ADE\u4E89key\u95EE\u9898</a></li><li><a href="#redis\u548Cmemcached\u7684\u533A\u522B">Redis\u548CMemcached\u7684\u533A\u522B</a></li></ul></nav><p>Redis \u662F\u5B8C\u5168\u5F00\u6E90\u514D\u8D39\u7684, \u9075\u5B88BSD\u534F\u8BAE, \u662F\u4E00\u4E2A\u9AD8\u6027\u80FD\u7684key-value\u6570\u636E\u5E93</p><h2 id="\u6570\u636E\u7C7B\u578B" tabindex="-1">\u6570\u636E\u7C7B\u578B <a class="header-anchor" href="#\u6570\u636E\u7C7B\u578B" aria-hidden="true">#</a></h2><p><strong>\u4E94\u79CD\u57FA\u672C\u6570\u636E\u7C7B\u578B</strong></p><p>String: \u5E38\u7528\u4E8E KV \u7F13\u5B58</p><p>Hash: \u5B58\u50A8\u7ED3\u6784\u5316\u6570\u636E, \u5982: \u4EA7\u54C1\u4FE1\u606F, \u7528\u6237\u4FE1\u606F\u7B49</p><p>List: \u5B58\u50A8\u5217\u8868, \u5982: \u7C89\u4E1D\u5217\u8868, \u6587\u7AE0\u8BC4\u8BBA\u5217\u8868\u7B49. \u53EF\u4EE5\u901A\u8FC7 lrange \u547D\u4EE4\u8FDB\u884C\u5206\u9875\u67E5\u8BE2</p><p>Set: \u5B58\u50A8\u53BB\u91CD\u5217\u8868, \u5982: \u7C89\u4E1D\u5217\u8868\u7B49. \u53EF\u4EE5\u57FA\u4E8E set \u73A9\u513F\u4EA4\u96C6, \u5E76\u96C6, \u5DEE\u96C6\u7684\u64CD\u4F5C. \u4F8B\u5982: \u6C42\u4E24\u4E2A\u4EBA\u7684\u5171\u540C\u597D\u53CB\u5217\u8868</p><p>Sorted Set: \u5B58\u50A8\u542B\u8BC4\u5206\u7684\u53BB\u91CD\u5217\u8868, \u5982: \u5404\u79CD\u6392\u884C\u699C</p><p><strong>\u9AD8\u7EA7\u6570\u636E\u7C7B\u578B</strong></p><p>Bitmaps, HyperLogLogs, GEO, Streams</p><h2 id="\u5E95\u5C42\u6570\u636E\u7ED3\u6784" tabindex="-1">\u5E95\u5C42\u6570\u636E\u7ED3\u6784 <a class="header-anchor" href="#\u5E95\u5C42\u6570\u636E\u7ED3\u6784" aria-hidden="true">#</a></h2><p>\u7B80\u5355\u52A8\u6001\u5B57\u7B26\u4E32(SDS)</p><p>\u94FE\u8868</p><p>\u5B57\u5178</p><p>\u8DF3\u8DC3\u8868</p><p>\u6574\u6570\u96C6\u5408</p><h2 id="\u4E3A\u4EC0\u4E48\u5FEB" tabindex="-1">\u4E3A\u4EC0\u4E48\u5FEB <a class="header-anchor" href="#\u4E3A\u4EC0\u4E48\u5FEB" aria-hidden="true">#</a></h2><p>Redis\u662F\u7EAF\u5185\u5B58\u64CD\u4F5C: \u6570\u636E\u5B58\u653E\u5728\u5185\u5B58\u4E2D, \u5185\u5B58\u7684\u54CD\u5E94\u65F6\u95F4\u5927\u7EA6\u662F100\u7EB3\u79D2, \u8FD9\u662FRedis\u6BCF\u79D2\u4E07\u4EBF\u7EA7\u522B\u8BBF\u95EE\u7684\u91CD\u8981\u57FA\u7840</p><p>\u975E\u963B\u585EI/O: Redis\u91C7\u7528epoll\u4F5C\u4E3AI/O\u591A\u8DEF\u590D\u7528\u6280\u672F\u7684\u5B9E\u73B0, \u518D\u52A0\u4E0ARedis\u81EA\u8EAB\u7684\u4E8B\u4EF6\u5904\u7406\u6A21\u578B\u5C06epoll\u4E2D\u7684\u8FDE\u63A5, \u8BFB\u5199, \u5173\u95ED\u90FD\u8F6C\u6362\u4E3A\u4E86\u65F6\u95F4, \u4E0D\u5728I/O\u4E0A\u6D6A\u8D39\u8FC7\u591A\u7684\u65F6\u95F4</p><p>\u5355\u7EBF\u7A0B\u907F\u514D\u4E86\u7EBF\u7A0B\u5207\u6362\u548C\u7ADE\u6001\u4EA7\u751F\u7684\u6D88\u8017</p><h2 id="\u5185\u5B58\u6DD8\u6C70" tabindex="-1">\u5185\u5B58\u6DD8\u6C70 <a class="header-anchor" href="#\u5185\u5B58\u6DD8\u6C70" aria-hidden="true">#</a></h2><p>Redis \u8FC7\u671F\u7B56\u7565\u662F: \u5B9A\u671F\u5220\u9664 + \u60F0\u6027\u5220\u9664</p><ul><li>\u6D88\u6781\u65B9\u6CD5 (passive way), \u5728\u4E3B\u952E\u88AB\u8BBF\u95EE\u65F6\u5982\u679C\u53D1\u73B0\u5B83\u5DF2\u7ECF\u5931\u6548, \u90A3\u4E48\u5C31\u5220\u9664\u5B83</li><li>\u4E3B\u52A8\u65B9\u6CD5 (active way), \u5B9A\u671F\u4ECE\u8BBE\u7F6E\u4E86\u5931\u6548\u65F6\u95F4\u7684\u4E3B\u952E\u4E2D\u9009\u62E9\u4E00\u90E8\u5206\u5931\u6548\u7684\u4E3B\u952E\u5220\u9664</li></ul><p>Redis \u5185\u5B58\u6DD8\u6C70\u7B56\u7565:</p><ul><li>noeviction - \u5F53\u5185\u5B58\u4F7F\u7528\u8FBE\u5230\u9608\u503C\u7684\u65F6\u5019, \u6240\u6709\u5F15\u8D77\u7533\u8BF7\u5185\u5B58\u7684\u547D\u4EE4\u4F1A\u62A5\u9519. \u8FD9\u662F Redis \u9ED8\u8BA4\u7684\u7B56\u7565</li><li>allkeys-lru - \u5728\u4E3B\u952E\u7A7A\u95F4\u4E2D, \u4F18\u5148\u79FB\u9664\u6700\u8FD1\u672A\u4F7F\u7528\u7684 key</li><li>allkeys-random - \u5728\u4E3B\u952E\u7A7A\u95F4\u4E2D, \u968F\u673A\u79FB\u9664\u67D0\u4E2A key</li><li>volatile-lru - \u5728\u8BBE\u7F6E\u4E86\u8FC7\u671F\u65F6\u95F4\u7684\u952E\u7A7A\u95F4\u4E2D, \u4F18\u5148\u79FB\u9664\u6700\u8FD1\u672A\u4F7F\u7528\u7684 key</li><li>volatile-random - \u5728\u8BBE\u7F6E\u4E86\u8FC7\u671F\u65F6\u95F4\u7684\u952E\u7A7A\u95F4\u4E2D, \u968F\u673A\u79FB\u9664\u67D0\u4E2A key</li><li>volatile-ttl - \u5728\u8BBE\u7F6E\u4E86\u8FC7\u671F\u65F6\u95F4\u7684\u952E\u7A7A\u95F4\u4E2D, \u5177\u6709\u66F4\u65E9\u8FC7\u671F\u65F6\u95F4\u7684 key \u4F18\u5148\u79FB\u9664</li></ul><h2 id="\u6301\u4E45\u5316" tabindex="-1">\u6301\u4E45\u5316 <a class="header-anchor" href="#\u6301\u4E45\u5316" aria-hidden="true">#</a></h2><p>RDB \u5373\u67D0\u4E00\u65F6\u523B\u7684\u4E8C\u8FDB\u5236\u6570\u636E\u5FEB\u7167</p><p>AOF \u4F1A\u5C06\u5199\u547D\u4EE4\u4E0D\u65AD\u8FFD\u52A0\u5230 AOF \u6587\u672C\u65E5\u5FD7\u672B\u5C3E</p><p>\u5EFA\u8BAE\u540C\u65F6\u4F7F\u7528 RDB \u548C AOF. \u7528 AOF \u6765\u4FDD\u8BC1\u6570\u636E\u4E0D\u4E22\u5931, \u4F5C\u4E3A\u6570\u636E\u6062\u590D\u7684\u7B2C\u4E00\u9009\u62E9; \u7528 RDB \u6765\u505A\u4E0D\u540C\u7A0B\u5EA6\u7684\u51B7\u5907, \u5728 AOF \u6587\u4EF6\u90FD\u4E22\u5931\u6216\u635F\u574F\u4E0D\u53EF\u7528\u7684\u65F6\u5019, \u8FD8\u53EF\u4EE5\u4F7F\u7528 RDB \u6765\u8FDB\u884C\u5FEB\u901F\u7684\u6570\u636E\u6062\u590D</p><h2 id="\u4E8B\u52A1" tabindex="-1">\u4E8B\u52A1 <a class="header-anchor" href="#\u4E8B\u52A1" aria-hidden="true">#</a></h2><p>Redis \u63D0\u4F9B\u7684\u4E0D\u662F\u4E25\u683C\u7684\u4E8B\u52A1, Redis \u53EA\u4FDD\u8BC1\u4E32\u884C\u6267\u884C\u547D\u4EE4, \u5E76\u4E14\u80FD\u4FDD\u8BC1\u5168\u90E8\u6267\u884C, \u4F46\u662F\u6267\u884C\u547D\u4EE4\u5931\u8D25\u65F6\u5E76\u4E0D\u4F1A\u56DE\u6EDA, \u800C\u662F\u4F1A\u7EE7\u7EED\u6267\u884C\u4E0B\u53BB</p><h2 id="\u7F13\u5B58\u7A7F\u900F-\u7F13\u5B58\u96EA\u5D29-\u7F13\u5B58\u51FB\u7A7F" tabindex="-1">\u7F13\u5B58\u7A7F\u900F \u7F13\u5B58\u96EA\u5D29 \u7F13\u5B58\u51FB\u7A7F <a class="header-anchor" href="#\u7F13\u5B58\u7A7F\u900F-\u7F13\u5B58\u96EA\u5D29-\u7F13\u5B58\u51FB\u7A7F" aria-hidden="true">#</a></h2><p><strong>\u7F13\u5B58\u7A7F\u900F</strong></p><p>\u7F13\u5B58\u7A7F\u900F\u662F\u6307\u67E5\u8BE2\u4E00\u4E2A\u4E00\u5B9A\u4E0D\u5B58\u5728\u7684\u6570\u636E, \u7531\u4E8E\u7F13\u5B58\u662F\u4E0D\u547D\u4E2D\u65F6\u88AB\u52A8\u5199\u7684, \u5E76\u4E14\u51FA\u4E8E\u5BB9\u9519\u8003\u8651, \u5982\u679C\u4ECE\u5B58\u50A8\u5C42\u67E5\u4E0D\u5230\u6570\u636E\u5219\u4E0D\u5199\u5165\u7F13\u5B58, \u8FD9\u5C06\u5BFC\u81F4\u8FD9\u4E2A\u4E0D\u5B58\u5728\u7684\u6570\u636E\u6BCF\u6B21\u8BF7\u6C42\u90FD\u8981\u5230\u5B58\u50A8\u5C42\u53BB\u67E5\u8BE2, \u5931\u53BB\u4E86\u7F13\u5B58\u7684\u610F\u4E49. \u5728\u6D41\u91CF\u5927\u65F6, \u53EF\u80FDDB\u5C31\u6302\u6389\u4E86, \u8981\u662F\u6709\u4EBA\u5229\u7528\u4E0D\u5B58\u5728\u7684key\u9891\u7E41\u653B\u51FB\u6211\u4EEC\u7684\u5E94\u7528, \u8FD9\u5C31\u662F\u6F0F\u6D1E</p><p>\u6709\u5F88\u591A\u79CD\u65B9\u6CD5\u53EF\u4EE5\u6709\u6548\u5730\u89E3\u51B3\u7F13\u5B58\u7A7F\u900F\u95EE\u9898, \u6700\u5E38\u89C1\u7684\u5219\u662F\u91C7\u7528\u5E03\u9686\u8FC7\u6EE4\u5668, \u5C06\u6240\u6709\u53EF\u80FD\u5B58\u5728\u7684\u6570\u636E\u54C8\u5E0C\u5230\u4E00\u4E2A\u8DB3\u591F\u5927\u7684bitmap\u4E2D, \u4E00\u4E2A\u4E00\u5B9A\u4E0D\u5B58\u5728\u7684\u6570\u636E\u4F1A\u88AB \u8FD9\u4E2Abitmap\u62E6\u622A\u6389, \u4ECE\u800C\u907F\u514D\u4E86\u5BF9\u5E95\u5C42\u5B58\u50A8\u7CFB\u7EDF\u7684\u67E5\u8BE2\u538B\u529B. \u53E6\u5916\u4E5F\u6709\u4E00\u4E2A\u66F4\u4E3A\u7B80\u5355\u7C97\u66B4\u7684\u65B9\u6CD5 (\u6211\u4EEC\u91C7\u7528\u7684\u5C31\u662F\u8FD9\u79CD), \u5982\u679C\u4E00\u4E2A\u67E5\u8BE2\u8FD4\u56DE\u7684\u6570\u636E\u4E3A\u7A7A (\u4E0D\u7BA1\u662F\u6570 \u636E\u4E0D\u5B58\u5728, \u8FD8\u662F\u7CFB\u7EDF\u6545\u969C), \u6211\u4EEC\u4ECD\u7136\u628A\u8FD9\u4E2A\u7A7A\u7ED3\u679C\u8FDB\u884C\u7F13\u5B58, \u4F46\u5B83\u7684\u8FC7\u671F\u65F6\u95F4\u4F1A\u5F88\u77ED, \u6700\u957F\u4E0D\u8D85\u8FC7\u4E94\u5206\u949F</p><p><strong>\u7F13\u5B58\u96EA\u5D29</strong></p><p>\u7F13\u5B58\u96EA\u5D29\u662F\u6307\u5728\u6211\u4EEC\u8BBE\u7F6E\u7F13\u5B58\u65F6\u91C7\u7528\u4E86\u76F8\u540C\u7684\u8FC7\u671F\u65F6\u95F4, \u5BFC\u81F4\u7F13\u5B58\u5728\u67D0\u4E00\u65F6\u523B\u540C\u65F6\u5931\u6548, \u8BF7\u6C42\u5168\u90E8\u8F6C\u53D1\u5230DB, DB\u77AC\u65F6\u538B\u529B\u8FC7\u91CD\u96EA\u5D29</p><p>\u7F13\u5B58\u5931\u6548\u65F6\u7684\u96EA\u5D29\u6548\u5E94\u5BF9\u5E95\u5C42\u7CFB\u7EDF\u7684\u51B2\u51FB\u975E\u5E38\u53EF\u6015. \u5927\u591A\u6570\u7CFB\u7EDF\u8BBE\u8BA1\u8005\u8003\u8651\u7528\u52A0\u9501\u6216\u8005\u961F\u5217\u7684\u65B9\u5F0F\u4FDD\u8BC1\u7F13\u5B58\u7684\u5355\u7EBF \u7A0B (\u8FDB\u7A0B)\u5199, \u4ECE\u800C\u907F\u514D\u5931\u6548\u65F6\u5927\u91CF\u7684\u5E76\u53D1\u8BF7\u6C42\u843D\u5230\u5E95\u5C42\u5B58\u50A8\u7CFB\u7EDF\u4E0A. \u8FD9\u91CC\u5206\u4EAB\u4E00\u4E2A\u7B80\u5355\u65B9\u6848\u5C31\u65F6\u8BB2\u7F13\u5B58\u5931\u6548\u65F6\u95F4\u5206\u6563\u5F00, \u6BD4\u5982\u6211\u4EEC\u53EF\u4EE5\u5728\u539F\u6709\u7684\u5931\u6548\u65F6\u95F4\u57FA\u7840\u4E0A\u589E\u52A0\u4E00\u4E2A\u968F\u673A\u503C, \u6BD4\u59821-5\u5206\u949F\u968F\u673A, \u8FD9\u6837\u6BCF\u4E00\u4E2A\u7F13\u5B58\u7684\u8FC7\u671F\u65F6\u95F4\u7684\u91CD\u590D\u7387\u5C31\u4F1A\u964D\u4F4E, \u5C31\u5F88\u96BE\u5F15\u53D1\u96C6\u4F53\u5931\u6548\u7684\u4E8B\u4EF6</p><p><strong>\u7F13\u5B58\u51FB\u7A7F</strong></p><p>\u5BF9\u4E8E\u4E00\u4E9B\u8BBE\u7F6E\u4E86\u8FC7\u671F\u65F6\u95F4\u7684key, \u5982\u679C\u8FD9\u4E9Bkey\u53EF\u80FD\u4F1A\u5728\u67D0\u4E9B\u65F6\u95F4\u70B9\u88AB\u8D85\u9AD8\u5E76\u53D1\u5730\u8BBF\u95EE, \u662F\u4E00\u79CD\u975E\u5E38 &quot;\u70ED\u70B9&quot; \u7684\u6570\u636E. \u8FD9\u4E2A\u65F6\u5019, \u9700\u8981\u8003\u8651\u4E00\u4E2A\u95EE\u9898: \u7F13\u5B58\u88AB &quot;\u51FB\u7A7F&quot; \u7684\u95EE\u9898, \u8FD9\u4E2A\u548C\u7F13\u5B58\u96EA\u5D29\u7684\u533A\u522B\u5728\u4E8E\u8FD9\u91CC\u9488\u5BF9\u67D0\u4E00key\u7F13\u5B58, \u524D\u8005\u5219\u662F\u5F88\u591Akey</p><p>\u7F13\u5B58\u5728\u67D0\u4E2A\u65F6\u95F4\u70B9\u8FC7\u671F\u7684\u65F6\u5019, \u6070\u597D\u5728\u8FD9\u4E2A\u65F6\u95F4\u70B9\u5BF9\u8FD9\u4E2AKey\u6709\u5927\u91CF\u7684\u5E76\u53D1\u8BF7\u6C42\u8FC7\u6765, \u8FD9\u4E9B\u8BF7\u6C42\u53D1\u73B0\u7F13\u5B58\u8FC7\u671F\u4E00\u822C\u90FD\u4F1A\u4ECE\u540E\u7AEFDB\u52A0\u8F7D\u6570\u636E\u5E76\u56DE\u8BBE\u5230\u7F13\u5B58, \u8FD9\u4E2A\u65F6\u5019\u5927\u5E76\u53D1\u7684\u8BF7\u6C42\u53EF\u80FD\u4F1A\u77AC\u95F4\u628A\u540E\u7AEFDB\u538B\u57AE</p><ul><li>\u4F7F\u7528\u4E92\u65A5\u9501 (mutex key)</li><li>&quot;\u63D0\u524D&quot; \u4F7F\u7528\u4E92\u65A5\u9501 (mutex key)</li><li>&quot;\u6C38\u8FDC\u4E0D\u8FC7\u671F&quot;</li><li>\u8D44\u6E90\u4FDD\u62A4</li></ul><h2 id="\u6D88\u606F\u4E22\u5931" tabindex="-1">\u6D88\u606F\u4E22\u5931 <a class="header-anchor" href="#\u6D88\u606F\u4E22\u5931" aria-hidden="true">#</a></h2><p>Redis Stream \u63D0\u4F9B\u4E86\u6D88\u606F\u7684\u6301\u4E45\u5316\u548C\u4E3B\u5907\u590D\u5236\u529F\u80FD, \u53EF\u4EE5\u8BA9\u4EFB\u4F55\u5BA2\u6237\u7AEF\u8BBF\u95EE\u4EFB\u4F55\u65F6\u523B\u7684\u6570\u636E, \u5E76\u4E14\u80FD\u8BB0\u4F4F\u6BCF\u4E00\u4E2A\u5BA2\u6237\u7AEF\u7684\u8BBF\u95EE\u4F4D\u7F6E, \u8FD8\u80FD\u4FDD\u8BC1\u6D88\u606F\u4E0D\u4E22\u5931</p><p>\u6D88\u8D39\u8005\u5904\u7406\u5B8C\u6D88\u606F\u540E, \u9700\u8981\u6267\u884C XACK \u547D\u4EE4\u544A\u77E5 Redis, \u8FD9\u65F6 Redis \u5C31\u4F1A\u628A\u8FD9\u6761\u6D88\u606F\u6807\u8BB0\u4E3A<strong>\u5904\u7406\u5B8C\u6210</strong>, \u5982\u679C\u6D88\u8D39\u8005\u5F02\u5E38\u5B95\u673A, \u80AF\u5B9A\u4E0D\u4F1A\u53D1\u9001 XACK, \u90A3\u4E48 Redis \u5C31\u4F1A\u4F9D\u65E7\u4FDD\u7559\u8FD9\u6761\u6D88\u606F, \u5F85\u8FD9\u7EC4\u6D88\u8D39\u8005\u91CD\u65B0\u4E0A\u7EBF\u540E, Redis \u5C31\u4F1A\u628A\u4E4B\u524D\u6CA1\u6709\u5904\u7406\u6210\u529F\u7684\u6570\u636E, \u91CD\u65B0\u53D1\u7ED9\u8FD9\u4E2A\u6D88\u8D39\u8005. \u8FD9\u6837\u4E00\u6765, \u5373\u4F7F\u6D88\u8D39\u8005\u5F02\u5E38, \u4E5F\u4E0D\u4F1A\u4E22\u5931\u6570\u636E</p><h2 id="\u66F4\u65B0\u7F13\u5B58\u7B56\u7565" tabindex="-1">\u66F4\u65B0\u7F13\u5B58\u7B56\u7565 <a class="header-anchor" href="#\u66F4\u65B0\u7F13\u5B58\u7B56\u7565" aria-hidden="true">#</a></h2><p><strong>Cache Aside</strong></p><p>\u8FD9\u662F\u6700\u5E38\u7528\u6700\u5E38\u7528\u7684pattern. \u5176\u5177\u4F53\u903B\u8F91\u5982\u4E0B:</p><p>\u5931\u6548: \u5E94\u7528\u7A0B\u5E8F\u5148\u4ECEcache\u53D6\u6570\u636E, \u6CA1\u6709\u5F97\u5230, \u5219\u4ECE\u6570\u636E\u5E93\u4E2D\u53D6\u6570\u636E, \u6210\u529F\u540E, \u653E\u5230\u7F13\u5B58\u4E2D</p><p>\u547D\u4E2D: \u5E94\u7528\u7A0B\u5E8F\u4ECEcache\u4E2D\u53D6\u6570\u636E, \u53D6\u5230\u540E\u8FD4\u56DE</p><p>\u66F4\u65B0: \u5148\u628A\u6570\u636E\u5B58\u5230\u6570\u636E\u5E93\u4E2D, \u6210\u529F\u540E, \u518D\u8BA9\u7F13\u5B58\u5931\u6548</p><p><strong>Read Through</strong></p><p>Read Through \u5957\u8DEF\u5C31\u662F\u5728\u67E5\u8BE2\u64CD\u4F5C\u4E2D\u66F4\u65B0\u7F13\u5B58, \u4E5F\u5C31\u662F\u8BF4, \u5F53\u7F13\u5B58\u5931\u6548\u7684\u65F6\u5019 (\u8FC7\u671F\u6216), Cache Aside \u662F\u7531\u8C03\u7528\u65B9\u8D1F\u8D23\u628A\u6570\u636E\u52A0\u8F7D\u5165\u7F13\u5B58, \u800C Read Through \u5219\u7528\u7F13\u5B58\u670D\u52A1\u81EA\u5DF1\u6765\u52A0\u8F7D, \u4ECE\u800C\u5BF9\u5E94\u7528\u65B9\u662F\u900F\u660E\u7684.</p><p><strong>Write Through</strong></p><p>Write Through \u5957\u8DEF\u548C Read Through \u76F8\u4EFF, \u4E0D\u8FC7\u662F\u5728\u66F4\u65B0\u6570\u636E\u65F6\u53D1\u751F. \u5F53\u6709\u6570\u636E\u66F4\u65B0\u7684\u65F6\u5019, \u5982\u679C\u6CA1\u6709\u547D\u4E2D\u7F13\u5B58, \u76F4\u63A5\u66F4\u65B0\u6570\u636E\u5E93, \u7136\u540E\u8FD4\u56DE. \u5982\u679C\u547D\u4E2D\u4E86\u7F13\u5B58, \u5219\u66F4\u65B0\u7F13\u5B58, \u7136\u540E\u518D\u7531Cache\u81EA\u5DF1\u66F4\u65B0\u6570\u636E\u5E93 (\u8FD9\u662F\u4E00\u4E2A\u540C\u6B65\u64CD\u4F5C)</p><p>\u4E0A\u9762\u7684Cache Aside\u5957\u8DEF\u4E2D, \u5E94\u7528\u4EE3\u7801\u9700\u8981\u7EF4\u62A4\u4E24\u4E2A\u6570\u636E\u5B58\u50A8, \u4E00\u4E2A\u662F\u7F13\u5B58 (Cache), \u4E00\u4E2A\u662F\u6570\u636E\u5E93 (Repository). \u6240\u4EE5, \u5E94\u7528\u7A0B\u5E8F\u6BD4\u8F83\u5570\u55E6. \u800C Read/Write Through \u5957\u8DEF\u662F\u628A\u66F4\u65B0\u6570\u636E\u5E93 (Repository) \u7684\u64CD\u4F5C\u7531\u7F13\u5B58\u81EA\u5DF1\u4EE3\u7406\u4E86, \u6240\u4EE5, \u5BF9\u4E8E\u5E94\u7528\u5C42\u6765\u8BF4, \u5C31\u7B80\u5355\u5F88\u591A\u4E86. \u53EF\u4EE5\u7406\u89E3\u4E3A, \u5E94\u7528\u8BA4\u4E3A\u540E\u7AEF\u5C31\u662F\u4E00\u4E2A\u5355\u4E00\u7684\u5B58\u50A8, \u800C\u5B58\u50A8\u81EA\u5DF1\u7EF4\u62A4\u81EA\u5DF1\u7684Cache</p><p><strong>Write Behind Caching Pattern</strong></p><p>Write Back \u5957\u8DEF, \u4E00\u53E5\u8BF4\u5C31\u662F, \u5728\u66F4\u65B0\u6570\u636E\u7684\u65F6\u5019, \u53EA\u66F4\u65B0\u7F13\u5B58, \u4E0D\u66F4\u65B0\u6570\u636E\u5E93, \u800C\u6211\u4EEC\u7684\u7F13\u5B58\u4F1A\u5F02\u6B65\u5730\u6279\u91CF\u66F4\u65B0\u6570\u636E\u5E93. \u8FD9\u4E2A\u8BBE\u8BA1\u7684\u597D\u5904\u5C31\u662F\u8BA9\u6570\u636E\u7684I/O\u64CD\u4F5C\u98DE\u5FEB\u65E0\u6BD4 (\u56E0\u4E3A\u76F4\u63A5\u64CD\u4F5C\u5185\u5B58\u561B), \u56E0\u4E3A\u5F02\u6B65, write backg \u8FD8\u53EF\u4EE5\u5408\u5E76\u5BF9\u540C\u4E00\u4E2A\u6570\u636E\u7684\u591A\u6B21\u64CD\u4F5C, \u6240\u4EE5\u6027\u80FD\u7684\u63D0\u9AD8\u662F\u76F8\u5F53\u53EF\u89C2\u7684. \u4E0A\u9762, \u6211\u4EEC\u6CA1\u6709\u8003\u8651\u7F13\u5B58 (Cache) \u548C\u6301\u4E45\u5C42 (Repository) \u7684\u6574\u4F53\u4E8B\u52A1\u7684\u95EE\u9898. \u6BD4\u5982, \u66F4\u65B0Cache\u6210\u529F, \u66F4\u65B0\u6570\u636E\u5E93\u5931\u8D25\u4E86\u600E\u4E48\u5417? \u6216\u662F\u53CD\u8FC7\u6765. \u5173\u4E8E\u8FD9\u4E2A\u4E8B, \u5982\u679C\u4F60\u9700\u8981\u5F3A\u4E00\u81F4\u6027, \u4F60\u9700\u8981\u4F7F\u7528 &quot;\u4E24\u9636\u6BB5\u63D0\u4EA4\u534F\u8BAE&quot; \u2014\u2014prepare, commit/rollback, \u6BD4\u5982Java 7 \u7684XAResource, \u8FD8\u6709MySQL 5.7\u7684 XA Transaction, \u6709\u4E9Bcache\u4E5F\u652F\u6301XA, \u6BD4\u5982EhCache. \u5F53\u7136, XA\u8FD9\u6837\u7684\u5F3A\u4E00\u81F4\u6027\u7684\u73A9\u6CD5\u4F1A\u5BFC\u81F4\u6027\u80FD\u4E0B\u964D.</p><h2 id="\u5982\u4F55\u89E3\u51B3redis\u7684\u5E76\u53D1\u7ADE\u4E89key\u95EE\u9898" tabindex="-1">\u5982\u4F55\u89E3\u51B3Redis\u7684\u5E76\u53D1\u7ADE\u4E89key\u95EE\u9898 <a class="header-anchor" href="#\u5982\u4F55\u89E3\u51B3redis\u7684\u5E76\u53D1\u7ADE\u4E89key\u95EE\u9898" aria-hidden="true">#</a></h2><p>(1)\u5982\u679C\u5BF9\u8FD9\u4E2Akey\u64CD\u4F5C, \u4E0D\u8981\u6C42\u987A\u5E8F</p><p>\u8FD9\u79CD\u60C5\u51B5\u4E0B, \u51C6\u5907\u4E00\u4E2A\u5206\u5E03\u5F0F\u9501, \u5927\u5BB6\u53BB\u62A2\u9501, \u62A2\u5230\u9501\u5C31\u505Aset\u64CD\u4F5C\u5373\u53EF, \u6BD4\u8F83\u7B80\u5355</p><p>(2)\u5982\u679C\u5BF9\u8FD9\u4E2Akey\u64CD\u4F5C, \u8981\u6C42\u987A\u5E8F</p><p>\u5047\u8BBE\u6709\u4E00\u4E2Akey1,\u7CFB\u7EDFA\u9700\u8981\u5C06key1\u8BBE\u7F6E\u4E3AvalueA,\u7CFB\u7EDFB\u9700\u8981\u5C06key1\u8BBE\u7F6E\u4E3AvalueB,\u7CFB\u7EDFC\u9700\u8981\u5C06key1\u8BBE\u7F6E\u4E3AvalueC.</p><p>\u671F\u671B\u6309\u7167key1\u7684value\u503C\u6309\u7167 valueA--&gt;valueB--&gt;valueC \u7684\u987A\u5E8F\u53D8\u5316. \u8FD9\u79CD\u65F6\u5019\u6211\u4EEC\u5728\u6570\u636E\u5199\u5165\u6570\u636E\u5E93\u7684\u65F6\u5019, \u9700\u8981\u4FDD\u5B58\u4E00\u4E2A\u65F6\u95F4\u6233</p><p>\u5047\u8BBE\u8FD9\u4F1A\u7CFB\u7EDFB\u5148\u62A2\u5230\u9501, \u5C06key1\u8BBE\u7F6E\u4E3A{valueB 3:05}. \u63A5\u4E0B\u6765\u7CFB\u7EDFA\u62A2\u5230\u9501, \u53D1\u73B0\u81EA\u5DF1\u7684valueA\u7684\u65F6\u95F4\u6233\u65E9\u4E8E\u7F13\u5B58\u4E2D\u7684\u65F6\u95F4\u6233, \u90A3\u5C31\u4E0D\u505Aset\u64CD\u4F5C\u4E86</p><p>\u5176\u4ED6\u65B9\u6CD5, \u6BD4\u5982\u5229\u7528\u961F\u5217, \u5C06set\u65B9\u6CD5\u53D8\u6210\u4E32\u884C\u8BBF\u95EE\u4E5F\u53EF\u4EE5.</p><h2 id="redis\u548Cmemcached\u7684\u533A\u522B" tabindex="-1">Redis\u548CMemcached\u7684\u533A\u522B <a class="header-anchor" href="#redis\u548Cmemcached\u7684\u533A\u522B" aria-hidden="true">#</a></h2><p>1, Redis\u548CMemcache\u90FD\u662F\u5C06\u6570\u636E\u5B58\u653E\u5728\u5185\u5B58\u4E2D, \u90FD\u662F\u5185\u5B58\u6570\u636E\u5E93. \u4E0D\u8FC7memcache\u8FD8\u53EF\u7528\u4E8E\u7F13\u5B58\u5176\u4ED6\u4E1C\u897F, \u4F8B\u5982\u56FE\u7247, \u89C6\u9891\u7B49\u7B49;</p><p>2, Redis\u4E0D\u4EC5\u4EC5\u652F\u6301\u7B80\u5355\u7684k/v\u7C7B\u578B\u7684\u6570\u636E, \u540C\u65F6\u8FD8\u63D0\u4F9Blist, set, hash\u7B49\u6570\u636E\u7ED3\u6784\u7684\u5B58\u50A8;</p><p>3, \u865A\u62DF\u5185\u5B58--Redis\u5F53\u7269\u7406\u5185\u5B58\u7528\u5B8C\u65F6, \u53EF\u4EE5\u5C06\u4E00\u4E9B\u5F88\u4E45\u6CA1\u7528\u5230\u7684 value \u4EA4\u6362\u5230\u78C1\u76D8;</p><p>4, \u8FC7\u671F\u7B56\u7565--memcache\u5728set\u65F6\u5C31\u6307\u5B9A, \u4F8B\u5982set key1 0 0 8,\u5373\u6C38\u4E0D\u8FC7\u671F. Redis\u53EF\u4EE5\u901A\u8FC7\u4F8B\u5982expire \u8BBE\u5B9A, \u4F8B\u5982expire name 10;</p><p>5, \u5206\u5E03\u5F0F--\u8BBE\u5B9Amemcache\u96C6\u7FA4, \u5229\u7528magent\u505A\u4E00\u4E3B\u591A\u4ECE;Redis\u53EF\u4EE5\u505A\u4E00\u4E3B\u591A\u4ECE. \u90FD\u53EF\u4EE5\u4E00\u4E3B\u4E00\u4ECE;</p><p>6, \u5B58\u50A8\u6570\u636E\u5B89\u5168--memcache\u6302\u6389\u540E, \u6570\u636E\u6CA1\u4E86; Redis\u53EF\u4EE5\u5B9A\u671F\u4FDD\u5B58\u5230\u78C1\u76D8 (\u6301\u4E45\u5316);</p><p>7, \u707E\u96BE\u6062\u590D--memcache\u6302\u6389\u540E, \u6570\u636E\u4E0D\u53EF\u6062\u590D; Redis\u6570\u636E\u4E22\u5931\u540E\u53EF\u4EE5\u901A\u8FC7aof\u6062\u590D;</p><p>8, Redis\u652F\u6301\u6570\u636E\u7684\u5907\u4EFD, \u5373master-slave\u6A21\u5F0F\u7684\u6570\u636E\u5907\u4EFD;</p><p>9, \u5E94\u7528\u573A\u666F\u4E0D\u4E00\u6837: Redis\u51FA\u6765\u4F5C\u4E3ANoSQL\u6570\u636E\u5E93\u4F7F\u7528\u5916, \u8FD8\u80FD\u7528\u505A\u6D88\u606F\u961F\u5217, \u6570\u636E\u5806\u6808\u548C\u6570\u636E\u7F13\u5B58\u7B49; Memcached\u9002\u5408\u4E8E\u7F13\u5B58SQL\u8BED\u53E5, \u6570\u636E\u96C6, \u7528\u6237\u4E34\u65F6\u6027\u6570\u636E, \u5EF6\u8FDF\u67E5\u8BE2\u6570\u636E\u548Csession\u7B49.</p>',78),s=[l];function p(d,h,o,c,n,u){return i(),a("div",null,s)}const m=e(t,[["render",p]]);export{R as __pageData,m as default};
