<span class="line"><span style="color:#F78C6C;">SELECT</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">*</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">FROM</span><span style="color:#A6ACCD;"> tag_post </span><span style="color:#F78C6C;">WHERE</span><span style="color:#A6ACCD;"> tag_id</span><span style="color:#89DDFF;">=</span><span style="color:#F78C6C;">1234</span><span style="color:#A6ACCD;">;</span></span>
